DECLARE
    v_device_id UUID;
    v_fingerprint TEXT;
    v_ip INET;
    v_device_type device_type;
BEGIN
    -- Validate user_id
    IF p_user_id IS NULL THEN
        RAISE EXCEPTION 'user_id cannot be null';
    END IF;

    -- Convert device type string to enum
    BEGIN
        v_device_type := p_device_type::device_type;
    EXCEPTION WHEN OTHERS THEN
        v_device_type := 'unknown';
    END;

    -- Convert IP string to INET
    IF p_ip_address IS NULL OR p_ip_address = '' OR p_ip_address = '0.0.0.0' THEN
        v_ip := NULL;
    ELSE
        BEGIN
            v_ip := p_ip_address::INET;
        EXCEPTION WHEN OTHERS THEN
            v_ip := NULL;
        END;
    END IF;

    -- Create fingerprint for matching
    v_fingerprint := md5(
        COALESCE(p_os_name, '') || 
        COALESCE(p_browser, '') || 
        COALESCE(LEFT(p_user_agent, 200), '')
    );

    -- Try to find existing device
    SELECT id INTO v_device_id
    FROM user_devices
    WHERE user_id = p_user_id
      AND md5(
          COALESCE(os_name, '') || 
          COALESCE(browser, '') || 
          COALESCE(LEFT(user_agent, 200), '')
      ) = v_fingerprint
    LIMIT 1;

    IF v_device_id IS NOT NULL THEN
        -- Update existing device
        UPDATE user_devices
        SET 
            device_type = COALESCE(v_device_type, device_type),
            os_name = COALESCE(NULLIF(p_os_name, ''), os_name),
            os_version = COALESCE(NULLIF(p_os_version, ''), os_version),
            browser = COALESCE(NULLIF(p_browser, ''), browser),
            browser_version = COALESCE(NULLIF(p_browser_version, ''), browser_version),
            ip_address = COALESCE(v_ip, ip_address),
            user_agent = COALESCE(NULLIF(p_user_agent, ''), user_agent),
            country = COALESCE(p_country, country),
            city = COALESCE(p_city, city),
            last_seen_at = NOW(),
            login_count = login_count + 1,
            is_current_device = TRUE
        WHERE id = v_device_id;

        -- Mark other devices as not current
        UPDATE user_devices 
        SET is_current_device = FALSE 
        WHERE user_id = p_user_id AND id != v_device_id;

        RETURN v_device_id::TEXT;
    ELSE
        -- Insert new device
        INSERT INTO user_devices (
            user_id, device_type, os_name, os_version, 
            browser, browser_version, ip_address, user_agent, 
            country, city, first_seen_at, last_seen_at, 
            login_count, is_current_device
        ) VALUES (
            p_user_id, COALESCE(v_device_type, 'unknown'),
            NULLIF(p_os_name, ''), NULLIF(p_os_version, ''),
            NULLIF(p_browser, ''), NULLIF(p_browser_version, ''),
            v_ip, NULLIF(p_user_agent, ''),
            p_country, p_city,
            NOW(), NOW(),
            1, TRUE
        )
        RETURNING id INTO v_device_id;

        -- Mark other devices as not current
        UPDATE user_devices 
        SET is_current_device = FALSE 
        WHERE user_id = p_user_id AND id != v_device_id;

        RETURN v_device_id::TEXT;
    END IF;

EXCEPTION WHEN OTHERS THEN
    RAISE WARNING 'upsert_user_device error: %', SQLERRM;
    RETURN NULL;
END;
