DECLARE
    v_current JSONB;
    v_lang_pages JSONB;
    v_page_words JSONB;
    v_result JSONB;
    v_is_highlighted BOOLEAN;
BEGIN
    -- إنشاء صف إذا لم يوجد
    INSERT INTO user_word_highlights (user_id, highlighted_words)
    VALUES (p_user_id, '{}'::jsonb)
    ON CONFLICT (user_id) DO NOTHING;

    -- جلب البيانات مع قفل
    SELECT highlighted_words INTO v_current
    FROM user_word_highlights
    WHERE user_id = p_user_id
    FOR UPDATE;

    v_current := COALESCE(v_current, '{}'::jsonb);
    v_lang_pages := COALESCE(v_current->p_language_code, '{}'::jsonb);
    v_page_words := COALESCE(v_lang_pages->p_page_id, '{}'::jsonb);

    -- Toggle
    IF v_page_words ? p_word_id THEN
        v_page_words := v_page_words - p_word_id;
        v_is_highlighted := false;
    ELSE
        v_page_words := jsonb_set(
            v_page_words,
            ARRAY[p_word_id],
            jsonb_build_object('at', NOW())
        );
        v_is_highlighted := true;
    END IF;

    -- تحديث البنية
    IF v_page_words = '{}'::jsonb THEN
        v_lang_pages := v_lang_pages - p_page_id;
    ELSE
        v_lang_pages := jsonb_set(v_lang_pages, ARRAY[p_page_id], v_page_words);
    END IF;

    IF v_lang_pages = '{}'::jsonb THEN
        v_result := v_current - p_language_code;
    ELSE
        v_result := jsonb_set(v_current, ARRAY[p_language_code], v_lang_pages);
    END IF;

    -- حفظ
    UPDATE user_word_highlights
    SET highlighted_words = v_result
    WHERE user_id = p_user_id;

    RETURN jsonb_build_object(
        'success', true,
        'is_now_highlighted', v_is_highlighted,
        'highlighted_words', v_result
    );
